import type { ComponentProps } from "solid-js";

import SearchIcon from "lucide-solid/icons/search";

import { Label } from "~/components/ui/label";
import {
  SidebarGroup,
  SidebarGroupContent,
  SidebarInput,
} from "~/components/ui/sidebar";

export function SearchForm(props: ComponentProps<"form">) {
  return (
    <form {...props}>
      <SidebarGroup class="py-0">
        <SidebarGroupContent class="relative">
          <Label for="search" class="sr-only">
            Search
          </Label>

          <SidebarInput
            id="search"
            placeholder="Search the docs..."
            class="pl-8"
          />
          <SearchIcon class="pointer-events-none absolute top-1/2 left-2 size-4 -translate-y-1/2 opacity-50 select-none" />
        </SidebarGroupContent>
      </SidebarGroup>
    </form>
  );
}

import { createSignal, For } from "solid-js";
import CheckIcon from "lucide-solid/icons/check";
import ChevronsUpDownIcn from "lucide-solid/icons/chevrons-up-down";
import GalleryVerticalEndIcon from "lucide-solid/icons/gallery-vertical-end";

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "~/components/ui/dropdown-menu";
import {
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
} from "~/components/ui/sidebar";

export function VersionSwitcher(props: {
  versions: string[];
  defaultVersion: string;
}) {
  const [selectedVersion, setSelectedVersion] = createSignal(
    props.defaultVersion,
  );

  return (
    <SidebarMenu>
      <SidebarMenuItem>
        <DropdownMenu sameWidth>
          <DropdownMenuTrigger
            as={SidebarMenuButton}
            size="lg"
            class="ui-expanded:bg-sidebar-accent ui-expanded:text-sidebar-accent-foreground"
          >
            <div class="flex aspect-square size-8 items-center justify-center rounded-lg bg-sidebar-primary text-sidebar-primary-foreground">
              <GalleryVerticalEndIcon class="size-4" />
            </div>
            <div class="flex flex-col gap-0.5 leading-none">
              <span class="font-medium">Documentation</span>
              <span class="">v{selectedVersion()}</span>
            </div>
            <ChevronsUpDownIcn class="ml-auto" />
          </DropdownMenuTrigger>
          <DropdownMenuContent>
            <For each={props.versions}>
              {(version) => (
                <DropdownMenuItem onSelect={() => setSelectedVersion(version)}>
                  v{version}{" "}
                  {version === selectedVersion() && (
                    <CheckIcon class="ml-auto" />
                  )}
                </DropdownMenuItem>
              )}
            </For>
          </DropdownMenuContent>
        </DropdownMenu>
      </SidebarMenuItem>
    </SidebarMenu>
  );
}
