import type { LucideIcon } from "lucide-solid";

import { For } from "solid-js";
import ChevronRight from "lucide-solid/icons/chevron-right";

import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "~/components/ui/collapsible";
import {
  SidebarGroup,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarMenuSub,
  SidebarMenuSubButton,
  SidebarMenuSubItem,
} from "~/components/ui/sidebar";

export function NavMain({
  items,
}: {
  items: {
    title: string;
    url: string;
    icon?: LucideIcon;
    isActive?: boolean;
    items?: {
      title: string;
      url: string;
    }[];
  }[];
}) {
  return (
    <SidebarGroup>
      <SidebarGroupLabel>Platform</SidebarGroupLabel>
      <SidebarMenu>
        <For each={items}>
          {(item) => (
            <Collapsible
              as={SidebarMenuItem}
              defaultOpen={item.isActive}
              class="group/collapsible"
            >
              <CollapsibleTrigger as={SidebarMenuButton} tooltip={item.title}>
                {item.icon && <item.icon />}
                <span>{item.title}</span>
                <ChevronRight class="ml-auto transition-transform duration-200 group-ui-expanded/collapsible:rotate-90" />
              </CollapsibleTrigger>
              <CollapsibleContent>
                <SidebarMenuSub>
                  <For each={item.items}>
                    {(subItem) => (
                      <SidebarMenuSubItem>
                        <SidebarMenuSubButton as="a" href={subItem.url}>
                          <span>{subItem.title}</span>
                        </SidebarMenuSubButton>
                      </SidebarMenuSubItem>
                    )}
                  </For>
                </SidebarMenuSub>
              </CollapsibleContent>
            </Collapsible>
          )}
        </For>
      </SidebarMenu>
    </SidebarGroup>
  );
}

import type { LucideIcon } from "lucide-solid";

import FolderIcon from "lucide-solid/icons/folder";
import ForwardIcon from "lucide-solid/icons/forward";
import MoreHorizontalIcon from "lucide-solid/icons/more-horizontal";
import Trash2Icon from "lucide-solid/icons/trash-2";

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "~/components/ui/dropdown-menu";
import {
  SidebarGroup,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuAction,
  SidebarMenuButton,
  SidebarMenuItem,
  useSidebar,
} from "~/components/ui/sidebar";

export function NavProjects({
  projects,
}: {
  projects: {
    name: string;
    url: string;
    icon: LucideIcon;
  }[];
}) {
  const { isMobile } = useSidebar();

  return (
    <SidebarGroup class="group-data-[collapsible=icon]:hidden">
      <SidebarGroupLabel>Projects</SidebarGroupLabel>
      <SidebarMenu>
        {projects.map((item) => (
          <SidebarMenuItem>
            <SidebarMenuButton as="a" href={item.url}>
              <item.icon />
              <span>{item.name}</span>
            </SidebarMenuButton>
            <DropdownMenu placement={isMobile() ? "bottom-end" : "right-start"}>
              <DropdownMenuTrigger as={SidebarMenuAction} showOnHover>
                <MoreHorizontalIcon />
                <span class="sr-only">More</span>
              </DropdownMenuTrigger>
              <DropdownMenuContent class="w-48 rounded-lg">
                <DropdownMenuItem>
                  <FolderIcon class="text-muted-foreground" />
                  <span>View Project</span>
                </DropdownMenuItem>
                <DropdownMenuItem>
                  <ForwardIcon class="text-muted-foreground" />
                  <span>Share Project</span>
                </DropdownMenuItem>
                <DropdownMenuSeparator />
                <DropdownMenuItem>
                  <Trash2Icon class="text-muted-foreground" />
                  <span>Delete Project</span>
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </SidebarMenuItem>
        ))}
        <SidebarMenuItem>
          <SidebarMenuButton class="text-sidebar-foreground/70">
            <MoreHorizontalIcon class="text-sidebar-foreground/70" />
            <span>More</span>
          </SidebarMenuButton>
        </SidebarMenuItem>
      </SidebarMenu>
    </SidebarGroup>
  );
}

import type { ComponentProps, JSX } from "solid-js";

import { createSignal, For } from "solid-js";
import { ChevronsUpDown, Plus } from "lucide-solid";

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuShortcut,
  DropdownMenuTrigger,
} from "~/components/ui/dropdown-menu";
import {
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  useSidebar,
} from "~/components/ui/sidebar";

export function TeamSwitcher(props: {
  teams: {
    name: string;
    logo: (props: ComponentProps<"svg">) => JSX.Element;
    plan: string;
  }[];
}) {
  const { isMobile } = useSidebar();
  const [activeTeam, setActiveTeam] = createSignal(props.teams[0]);
  const Logo = activeTeam().logo;

  if (!activeTeam) {
    return null;
  }

  return (
    <SidebarMenu>
      <SidebarMenuItem>
        <DropdownMenu
          placement={isMobile() ? "bottom-start" : "right-start"}
          gutter={4}
          sameWidth
        >
          <DropdownMenuTrigger
            as={SidebarMenuButton}
            size="lg"
            class="ui-expanded:bg-sidebar-accent ui-expanded:text-sidebar-accent-foreground"
          >
            <div class="flex aspect-square size-8 items-center justify-center rounded-lg bg-sidebar-primary text-sidebar-primary-foreground">
              <Logo class="size-4" />
            </div>
            <div class="grid flex-1 text-left text-sm leading-tight">
              <span class="truncate font-medium">{activeTeam().name}</span>
              <span class="truncate text-xs">{activeTeam().plan}</span>
            </div>
            <ChevronsUpDown class="ml-auto" />
          </DropdownMenuTrigger>
          <DropdownMenuContent class="min-w-56 rounded-lg">
            <DropdownMenuLabel class="text-xs text-muted-foreground">
              Teams
            </DropdownMenuLabel>
            <For each={props.teams}>
              {(team, index) => (
                <DropdownMenuItem
                  onClick={() => setActiveTeam(team)}
                  class="gap-2 p-2"
                >
                  <div class="flex size-6 items-center justify-center rounded-md border">
                    <team.logo class="size-3.5 shrink-0" />
                  </div>
                  {team.name}
                  <DropdownMenuShortcut>âŒ˜{index() + 1}</DropdownMenuShortcut>
                </DropdownMenuItem>
              )}
            </For>
            <DropdownMenuSeparator />
            <DropdownMenuItem class="gap-2 p-2">
              <div class="flex size-6 items-center justify-center rounded-md border bg-transparent">
                <Plus class="size-4" />
              </div>
              <div class="font-medium text-muted-foreground">Add team</div>
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </SidebarMenuItem>
    </SidebarMenu>
  );
}
